import React, { useState } from "react";

const Message = (props) => {
  const [score, setScore] = useState(20);
  console.log(props.final);
  console.log(props.random);

  let newmessage;

  const updateScore = (props) => {
    if (props.final !== props.random) {
      setScore(10);
    } else setScore(9);
  };

  const decrement = () => {
    setScore((prevScore) => ({
      score: prevScore - 1,
    }));
  };

  if (!props.final) {
    newmessage = "Start guessing...";
    score = 20;
  } else if (props.final > props.random) {
    newmessage = "Too high, try again!";
    decrement();
  } else if (props.final === props.random) {
    newmessage = "You win!";
  } else {
    newmessage = "Too low, try again!";
    score = score--;
  }

  return (
    <section className="right">
      <p className="message">{newmessage}</p>
      <p className="label-score">
        ðŸ’¯ Score: <span className="score">{updateScore()}</span>
      </p>
      <p className="label-highscore">
        ðŸ¥‡ Highscore: <span className="highscore">0</span>
      </p>
    </section>
  );
};

export default Message;
